// 타이핑될 텍스트
const text = "예G는 회사를 통해 5G를 꿈꿉니다.";
const typingTxt = document.querySelector(".about");
let typingIdx=0;

function typing(){
    if(typingIdx != text.length -1 ){
        typingTxt.textContent += text[typingIdx++]
    }
}

setInterval(typing,100)



